import React, {useState} from "react";
import './App.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch,
  useParams
} from "react-router-dom";
import Footer from "./Components/Footer";
import Header from "./Components/Header";
import VisitorView from './Components/VisitorView';
import Login from './Components/Login';
import Homepage from "./Components/Homepage";
import ProtectedRoute from './Components/ProtectedRoute';
import UserView from "./Components/UserView";
import axios from 'axios';
import constants from './Components/authentication/constants.json';
import Auth from './Components/authentication/Auth';

function App() {
  const [isUserLogged, setIsUserLogged] = userState(false);

  function processLoginClick(history) {
    console.log("Click");

    axios.post('/login', {}, {});

    setIsUserLogged(true);
    history.push('/UserView');
  }

    return (
      <Router>
        <Header/>
          <Route path="/visitorview">
            <VisitorView />
          </Route>
          <Route path="/Login" exact render={
          (routeProps) =>
            <Login {...routeProps} onLoginClick={processLoginClick}/>
          }
          />
          <ProtectedRoute path="/UserView" exact component ={UserView} isUserLogged={isUserLogged}/>
        <Homepage/>  
        <Footer/> 
      </Router> 
    )
  }


